import React, { useContext } from "react";

const BudgetsContexts = React.createContext();

//allows us to use context
export const useBudgets = () => {
  return useContext(BudgetsContexts);
};

//Budget Provider that we wrap pur entire application in
export const BudgetsProvider = ({ children }) => {
  return (
    <BudgetsContexts.Provider value={{}}>{children}</BudgetsContexts.Provider>
  );
};
