import React from "react";
import { useBudgets } from "../contexts/BudgetsContexts";
import BudgetCard from "./BudgetCard";

export default function TotalBudgetCard() {
  const { expense } = useBudgets();
  const amount = expense.reduce((total, expense) => total + expense.amount, 0);

  const max = expenses.reduce((total, budget) => total + budget.max, 0);

  if (max === 0) return null;

  return <BudgetCard grey name="Total" amount={amount} max={max} />;
}
